sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
// 24h後(1日後)の日時を取得
var after24h = new GlideDateTime();
after24h.addDaysLocalTime(1);

//gs.info("Find NeedIt Tasks Due Soon Execute!");
//gs.info("rightNow:" + rightNow);
//gs.info("after24h:" + after24h);

// 期限が現在から24h後までの間のレコードを抽出
// Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var duesoonNITasks = new GlideRecord('x_58872_needit_needit_task');
duesoonNITasks.addQuery('due_date','>=',rightNow);
duesoonNITasks.addQuery('due_date','<=',after24h);
duesoonNITasks.addQuery('state','!=',3);

duesoonNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while(duesoonNITasks.next()){
	gs.info("NeedIt Task due soon record = " + duesoonNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',duesoonNITasks,duesoonNITasks.number,gs.getUserName());

}
